import React, { Component, seState, useEffect, useRef } from 'react';
import RoboChart from '@postlight/react-google-sheet-to-chart';
import './App.css';
import './index.css';

import { CSSTransition } from 'react-transition-group';

import { ReactComponent as BellIcon } from './icons/bell.svg';
import { ReactComponent as MessengerIcon } from './icons/messenger.svg';
import { ReactComponent as CaretIcon } from './icons/caret.svg';
import { ReactComponent as PlusIcon } from './icons/plus.svg';
import { ReactComponent as CogIcon } from './icons/cog.svg';
import { ReactComponent as ChevronIcon } from './icons/chevron.svg';
import { ReactComponent as ArrowIcon } from './icons/arrow.svg';
import { ReactComponent as BoltIcon } from './icons/bolt.svg';
import Tabletop from 'tabletop';
// const style = { width: '1200px', margin: '0 auto' }; //Horizontally scrollable

class App extends Component {

    constructor() {
        super()
        this.state = {
            data: []
        }
    }
    componentDidMount() {
        Tabletop.init({
            key: '1Bh5AV7LwiiWOlK6G-kVDX8YiWJNEyLrrYU6WEYnb_lg',
            callback: googleData => {
                console.log('google sheet data --->', googleData)
            },
            simpleSheet: true
        })
    }

    render() {
        const { data } = this.state
        return (
            <div className="App">
                <header className="App-header">
                    <h1 className="App-title">React + Google Sheets Demo</h1>
                </header>
                <div id="employee-details">
                    {
                        data.map(obj => {
                            return (
                                <div key={obj.visitors}>
                                    <p>{obj.fecha}</p>
                                    <p>{obj.visitors}</p>
                                </div>
                            )
                        })
                    }
                </div>
            </div>
        );
    }
}


